  @override
  Future<List<Drug>> getAllDrugs() async {
    return [
      ..._painRelieversAndAntipyretics(),
      ..._antibiotics(),
      ..._respiratoryDrugs(),
      ..._gastrointestinalDrugs(),
      ..._allergyAndSkinDrugs(),
      ..._vitaminsAndSupplements(),
      ..._cardiovascularDrugs(),
      ..._diabetesDrugs(),
      ..._neurologicalDrugs(),
      ..._psychiatricDrugs(),
      ..._hormonalDrugs(),
      ..._urologyDrugs(),
      ..._ophthalmicDrugs(),
      ..._entDrugs(),
      ..._musculoskeletalDrugs(),
      ..._antiparasiticDrugs(),
      ..._emergencyDrugs(),
      ..._pediatricDrugs(),
      ..._dermatologyDrugs(),
      ..._immuneAndCancerDrugs(),
    ];
  }

  @override
  Future<List<Drug>> searchDrugs(String query) async {
    final allDrugs = await getAllDrugs();
    if (query.isEmpty) return allDrugs;
    
    final lowercaseQuery = query.toLowerCase();
    return allDrugs.where((drug) {
      return drug.englishName.toLowerCase().contains(lowercaseQuery) ||
             drug.arabicName.contains(query) ||
             drug.category.toLowerCase().contains(lowercaseQuery);
    }).toList();
  }

  @override
  Future<Drug?> getDrugById(String id) async {
    final allDrugs = await getAllDrugs();
    try {
      return allDrugs.firstWhere((drug) => drug.id == id);
    } catch (e) {
      return null;
    }
  }

