  Drug _createDrug({
    required String id,
    required String englishName,
    required String arabicName,
    required String category,
    required DrugType type,
    required String description,
    required String dosage,
    required String sideEffects,
    required String contraindications,
    double concentrationMg = 0,
    double concentrationMl = 0,
    double minDosePerKg = 0,
    double maxDosePerKg = 0,
    double maxDailyDoseMg = 0,
    List<String> warningDiseases = const [],
    List<String> warningAllergies = const [],
  }) {
    return Drug(
      id: id,
      englishName: englishName,
      arabicName: arabicName,
      category: category,
      type: type,
      description: description,
      dosage: dosage,
      sideEffects: sideEffects,
      contraindications: contraindications,
      concentrationMg: concentrationMg,
      concentrationMl: concentrationMl,
      minDosePerKg: minDosePerKg,
      maxDosePerKg: maxDosePerKg,
      maxDailyDoseMg: maxDailyDoseMg,
      warningDiseases: warningDiseases,
      warningAllergies: warningAllergies,
    );
  }
